mapper.select:
SELECT d.*,c.name AS category_name FROM dish d LEFT JOIN category c ON d.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by d.create_time desc

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select *
from dish

    and name like concat('%',#{name},'%')
    and category_id = #{categoryId}
    and status = #{status}

order by create_time desc

select count(id) from dish


        and status = #{status}


        and category_id = #{categoryId}
